/*!
 * Site scripts
 * Lauren Vachon
 */

$( document ).ready(function() {

//////////////
// SITEWIDE //
//////////////

  // Hide Header on on scroll down
    var didScroll;
    var lastScrollTop = 0;
    var delta = 5;
    var navbarHeight = $('header').outerHeight();
    $(window).scroll(function(event){
        didScroll = true;
    });
    setInterval(function() {
        if (didScroll) {
            hasScrolled();
            didScroll = false;
        }
    }, 50);

    function hasScrolled() {
        var st = $(this).scrollTop();
        if(Math.abs(lastScrollTop - st) <= delta)
            return;
        if (st > lastScrollTop && st > navbarHeight){
            $('header').removeClass('nav-down').addClass('nav-up');
        } else {
            if(st + $(window).height() < $(document).height()) {
                $('header').removeClass('nav-up').addClass('nav-down');
            }
        }
        lastScrollTop = st;
    }


  // AOS
  AOS.init({
    duration: 1200,
  })


//////////////
// HOMEPAGE //
//////////////

if($("body").hasClass("homepage")){

  // show side nav on scroll to section 2
  var heroHeight = $('#section1').height();
  $(window).scroll(function() {
    if ($(this).scrollTop()> heroHeight - 400)
     {
        $('.navbar').css('right','0');
        $('.navbar').addClass('moveOut');
     }
    else
     {
      $('.navbar').css('right','-80px');
      $('.navbar').removeClass('moveOut');
     }
   });

  // Add smooth scrolling on all links inside the navbar
  $(".soSmooth").on('click', function(event) {
    if (this.hash !== "") {
      event.preventDefault();
      var hash = this.hash;
      $('html, body').animate({
        scrollTop: $(hash).offset().top
      }, 800, function(){
        window.location.hash = hash;
      });
    }
  });

}

if($("body").hasClass("journal")){

  $(function () {
	var $content = $('#jsonContent');
	var data = {
		rss_url: 'https://medium.com/feed/@laurenvachon'
	};
	$.get('https://api.rss2json.com/v1/api.json', data, function (response) {
		if (response.status == 'ok') {
			var output = '';
			$.each(response.items, function (k, item) {
				var visibleSm;
				if(k < 3){
					visibleSm = '';
				 } else {
					 visibleSm = ' visible-sm';
				 }
				output += '<div class="col-sm-6 col-md-4' + visibleSm + '">';
				output += '<div class="blog-post"><div>';
				// output += '<h4 class="date">' + $.format.date(item.pubDate, "dd<br>MMM") + "</h4>";
				var tagIndex = item.description.indexOf('<img'); // Find where the img tag starts
				var srcIndex = item.description.substring(tagIndex).indexOf('src=') + tagIndex; // Find where the src attribute starts
				var srcStart = srcIndex + 5; // Find where the actual image URL starts; 5 for the length of 'src="'
				var srcEnd = item.description.substring(srcStart).indexOf('"') + srcStart; // Find where the URL ends
				var src = item.description.substring(srcStart, srcEnd); // Extract just the URL
				output += '<div class="blog-element"><img class="img-responsive" src="' + src + '" width="360px" height="240px"></div></div>';
				output += '<div class="blog-content"><h3>' + item.title + '</h3>';
				output += '<div class="post-meta"><span>By ' + item.author + '</span></div>';
				var yourString = item.description.replace(/<img[^>]*>/g,""); //replace with your string.
				var maxLength = 120 // maximum number of characters to extract
				//trim the string to the maximum length
				var trimmedString = yourString.substr(0, maxLength);
				//re-trim if we are in the middle of a word
				trimmedString = trimmedString.substr(0, Math.min(trimmedString.length, trimmedString.lastIndexOf(" ")))
				output += '<p>' + trimmedString + '...</p>';
        output += '<p><a href="'+ item.link + '"> Continue Reading </a></p>';
				output += '</div></div></div>';
				return k < 3;
			});
			$content.html(output);
		}
	});
});




}

if($("body").hasClass("photography")){

  // disable right click
  $('img').on("contextmenu",function(e){
      return false;
  });

  // masonry
  $(window).load(function(){
    $('.grid').fadeIn(3000).masonry({
        itemSelector: '.grid-item',
        columnWidth: '.grid-sizer',
        percentPosition: true
    });
  })

  // fancybox
  $("[data-fancybox]").fancybox({
  	speed : 630,
    image : {
		    protect : true
	  }
  });

}

});
